    console.log(form)
  }

  return (
    <View style={estilo.background}>
      <View style={estilo.container}>
        <Text style={estilo.titulo2}>Mensalidades</Text>
        <View
          style={{
            flexDirection: "row",
            justifyContent: "center",
            flexWrap: "wrap",
            width: "90%",
            marginBottom: "2%",
          }}
        >
          <TouchableOpacity
            style={{
              flexDirection: "row",
              backgroundColor: "#004A85",
              width: "auto",
              height: 45,
              alignItems: "center",
              justifyContent: "center",
              borderRadius: 10,
              padding: 2,
              marginRight: "10%",
            }}
            onPress={() => add()}
          >
            <Ionicons name="add-outline" size={15} color={"white"} />
            <Text style={estilo.textBtn2}>Adicionar mensalidade</Text>
          </TouchableOpacity>

          <TextInput
            style={estilo.inputF}
            placeholder="Filtro"
            value={filtro}
            onChangeText={(text) => setFiltro(text)}
          />
          <TouchableOpacity
            style={{
              backgroundColor: "#004A85",
              width: "auto",
              height: 45,
              alignItems: "center",
              justifyContent: "center",
              borderRadius: 10,
              marginLeft: "0.5%",
              padding: "1%",
            }}
            onPress={() => filtrado()}
          >
            <Ionicons name="search-outline" size={25} color={"white"} />
          </TouchableOpacity>
          <TouchableOpacity
            style={{
              backgroundColor: "#004A85",
              width: "auto",
              height: 45,
              alignItems: "center",
              justifyContent: "center",
              borderRadius: 10,
              marginLeft: "1.5%",
            }}
            onPress={() => veiculos()}
          >
            <Text style={estilo.textBtn2}>Mostrar tudo</Text>
          </TouchableOpacity>
        </View>
        <FlatList
          style={estilo.flatList}
          data={mensalidade}
          renderItem={renderItem}
          keyExtractor={(item) => item.id}
        />
        <Modal
          animationType="slide"
          transparent={true}
          visible={visible}
          onRequestClose={() => {
            setModalVisible(!visible);
          }}
        >
          <View style={estilo.modal}>
            <Ionicons
              name="close-circle-outline"
              size={25}
              color={"black"}
              onPress={() => {
                setModalVisible(!visible);
                setForm({
                  Modelo: "",
                  Consumo: "",
                  Capacidade: "",
                  Fabricacao: "",
                  Status: "",
                  Regularizacao: "",
                });
              }}
              style={{ alignSelf: "flex-end", marginTop: "15%" }}
            />

            <Text style={estilo.titulo2}>{submit + " dos dados"}</Text>
            <SafeAreaView style={{ width: "100%", height: "90%" }}>
              <ScrollView contentContainerStyle={estilo.ScrollView}>
                <SelectDropdown
                  data={passageiro}
                  value={form.Nome}
                  onSelect={(selectedItem, index) => {
                    updateForm({ Nome: selectedItem });
                  }}
                  defaultButtonText={"Passageiro(a)"}
                  buttonTextAfterSelection={(selectedItem, index) => {
                    return selectedItem;
                  }}
                  rowTextForSelection={(item, index) => {
                    return item;
                  }}
                  renderDropdownIcon={(isOpened) => {
                    return (
                      <Ionicons
                        name={isOpened ? "chevron-up" : "chevron-down"}
                        color={"#444"}
                        size={18}
                      />
                    );
                  }}
                  dropdownIconPosition={"right"}
                  dropdownStyle={estilo.DropdownStyle}
                  buttonStyle={estilo.drop}
                />
                {inputs.map((input, key) => (
                  <View style={style.linha} key={key}>
                    <Text style={estilo.txt}>{"Mês " + (key + 1) + ":"}</Text>
                    <TextInputMask
                      style={style.input}
                      type={"money"}
                      placeholder={"Digite o valor"}
                      value={input.valor}
                      onChangeText={(text) => inputHandler(text, key)}
                    />

                    <TouchableOpacity onPress={() => deleteHandler(key)}>
                      <Ionicons
                        name="trash-outline"
                        size={25}
                        color={"black"}
                      />
                    </TouchableOpacity>
                  </View>
                ))}
                <TouchableOpacity style={style.btn} onPress={addHandler}>
                  <Ionicons name={"add-outline"} color={"white"} size={25} />
                  <Text style={estilo.textBtn}>Adicionar mês</Text>
                </TouchableOpacity>
                <TouchableOpacity style={estilo.btn} onPress={onSubmit}>
                  <Text style={estilo.textBtn}>Salvar</Text>
                </TouchableOpacity>
              </ScrollView>
            </SafeAreaView>
          </View>
        </Modal>
      </View>
    </View>
  );
}

