import { StyleSheet, Text, View } from "react-native";
import { React, useState } from "react";
import DateTimePicker from '@react-native-community/datetimepicker';

export default function listaViagem() {
  const [data, setData] = useState(new Date(1598051830000));
  const [show, setShow] = useState(false);

  const onChange = (event, selectedDate) => {
    setData(selectedDate);
  }

  const showdatePicker = () => {
    setShow(true);
  }

  return (
    <View style={styles.container}>
      <View style={styles.container2}>
        <Text style={{ padding: 10, fontWeight: "bold", fontSize: 20 }}>
          Registre o horário de sua viagem
        </Text>
        <View>
        <TouchableOpacity
            style={styles.btn}
            onPress={showdatePicker}
          >
            <Text style={styles.textBtn}>Selecionar data</Text>
          </TouchableOpacity>
          <DateTimePicker
            testID="dareTimePicker"
            mode={mode}
            value = {data}
            is24Hour={true}
            display = 'default'
            onChange={onChange}
          />

        </View>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: "#DFDFDF",
  },
  container2: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: "#A4E3F9",
    width: "90%",
    borderRadius: 15,
    borderWidth: 1,
    borderColor: "#004A85",
    marginTop: "1%",
    marginBottom: "5%",
  },
  data:{
    backgroundColor: "#ffffff",
    alignItems: "center",
    padding: 10,
    height: "20%",
    width: "30%",
  },
});
