import { StyleSheet, Text, View } from "react-native";
import { Card, Title, Paragraph } from "react-native-paper";
import { React } from "react";
import { NavigationContainer } from "@react-navigation/native";
import { Ionicons } from "@expo/vector-icons";

export default function Home() {
  return (
    <View style={styles.container}>
      <View style={styles.container2}>
        <Text style={{ padding: 10, fontWeight: "bold", fontSize: 20 }}>
          Seja bem vindo(a)!
        </Text>
        <View style={styles.infoStyle}>
          <Text style={styles.titleStyle}>oi</Text>
          <Text style={styles.categoryStyle}>oii</Text>
        </View>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: "#DFDFDF",
  },
  container2: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: "#A4E3F9",
    width: "90%",
    borderRadius: 15,
    borderWidth: 1,
    borderColor: "#004A85",
  },
  card: {
    flex: 1,
    alignItems: "center",
    backgroundColor: "#DFDFDF",
    width: "20%",
    maxHeight: "20%",
    borderRadius: 15,
    borderWidth: 1,
    borderColor: "#004A85",
    padding: 15,
    cardContainer: {
      width: deviceWidth - offset,
      backgroundColor: '#a29bfe',
      height: 200,
      borderRadius: radius,
  
      shadowColor: '#000',
      shadowOffset: {
        width: 5,
        height: 5,
      },
      shadowOpacity: 0.75,
      shadowRadius: 5,
      elevation: 9,
    },
    imageStyle: {
      height: 130,
      width: deviceWidth - offset,
      borderTopLeftRadius: radius,
      borderTopRightRadius: radius,
      opacity: 0.9,
      alignContent: 'center',
      alignSelf: 'center',
    },
    titleStyle: {
      fontSize: 20,
      fontWeight: '800',
    },
    categoryStyle: {
      fontWeight: '200',
    },
    infoStyle: {
      marginHorizontal: 10,
      marginVertical: 5,
    },
  },
});
