import {
  StyleSheet,
  Text,
  View,
  TouchableOpacity,
  TextInput,
} from "react-native";
import { Card } from "react-native-paper";
import React from "react";
import { useForm, Controller } from "react-hook-form";

export default function Home() {
  const {
    control,
    handleSubmit,
    formState: { errors },
  } = useForm({});

  function CadPassageiro(data) {
    console.log(data);
  }

  return (
    <View style={styles.container}>
      <View style={styles.container2}>
        <Card style={styles.card}>
          <View>
            <Text style={{ fontSize: 30 }}>Cadastro de Passageiros</Text>
          </View>
          <View>
            <Controller
              control={control}
              name="CadastroPassageiro"
              render={({field: {onChange, onBlur, value}}) => (
                <TextInput
                  style={styles.input}
                  onChangeText={onChange}
                  onBlur={onBlur}
                  value={value}
                  placeholder="Digite o nome completo"
                />
              )}
            />

            <TouchableOpacity onPress={handleSubmit(CadPassageiro)}>
              <Text>Cadastrar</Text>
            </TouchableOpacity>
          </View>
        </Card>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: "#DFDFDF",
  },
  container2: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: "#A4E3F9",
    width: "90%",
    maxHeight: "95%",
    borderRadius: 15,
    borderWidth: 1,
    borderColor: "#004A85",
  },
  card: {
    flex: 1,
    backgroundColor: "#DFDFDF",
    width: "90%",
    maxHeight: "98%",
    borderRadius: 15,
    borderWidth: 1,
    borderColor: "#004A85",
    padding: 15,
  },
});
