                  style={{
                    backgroundColor: "#F5F5F5",
                    width: "90%",
                    color: "#000000",
                    marginBottom: 15,
                    fontSize: RFPercentage(1.5),
                    borderRadius: 10,
                    borderWidth: 0.5,
                    padding: 10,
                    borderColor: "#004A85",
                    shadowColor: "black",
                    shadowOffset: {
                      width: 0,
                      height: 5,
                    },
                    shadowOpacity: 0.58,
                    shadowRadius: 5,
                    elevation: 10,
                  }}
                  type={"cel-phone"}
                  placeholder="Digite o contato"
                  value={form.Contato}
                  onChangeText={(e) => updateForm({ Contato: e })}
                  keyboardType="numeric"
                  maxLength={15}
