                  style={{
                    width: "90%",
                    alignItems: "center",
                    flexDirection: "row",
                    justifyContent: "center",
                    marginBottom: 15,
                  }}
                >
                  <TextInputMask
                    style={{
                      backgroundColor: "#F5F5F5",
                      width: "90%",
                      color: "#000000",
                      fontSize: RFPercentage(1.5),
                      borderRadius: 10,
                      borderWidth: 0.5,
                      padding: 10,
                      borderColor: "#004A85",
                      shadowColor: "black",
                      shadowOffset: {
                        width: 0,
                        height: 5,
                      },
                      shadowOpacity: 0.58,
                      shadowRadius: 5,
                      elevation: 10,
                    }}
                    type={"zip-code"}
                    placeholder="Digite o CEP"
                    value={form.CEP}
                    onChangeText={(e) => updateForm({ CEP: e })}
                    keyboardType="numeric"
                    maxLength={10}
                  />

                  <TouchableOpacity
                    style={{
                      backgroundColor: "#004A85",
                      width: "auto",
                      alignItems: "center",
                      justifyContent: "center",
                      borderRadius: 10,
                      marginLeft: "1%",
                      padding: "1%",
                    }}
                    onPress={() => executaPesquisa()}
                  >
                    <Ionicons name="search-outline" size={15} color={"white"} />
                  </TouchableOpacity>
                </View>
                {dropcfp && (
                  <View style={{ width: "100%", alignItems: "center" }}>
                    <TextInput
                      style={estilo.input}
                      placeholder="Digite o bairro"
                      value={form.Bairro}
                      onChangeText={(e) => updateForm({ Bairro: e })}
                    />
                    <TextInput
                      style={estilo.input}
                      placeholder="Digite a rua"
                      value={form.Rua}
                      onChangeText={(e) => updateForm({ Rua: e })}
                    />
                    <TextInput
                      style={estilo.input}
                      placeholder="Digite o número"
                      value={form.Numero}
                      onChangeText={(e) => updateForm({ Numero: e })}
                    />
                    <TextInput
                      style={estilo.input}
                      placeholder="Digite o complemento"
                      value={form.Complemento}
                      onChangeText={(e) => updateForm({ Complemento: e })}
                    />
                  </View>
                )}
                <TouchableOpacity
                  style={{
                    backgroundColor: "#004A85",
                    width: "90%",
                    height: 45,
                    alignItems: "center",
                    justifyContent: "center",
                    borderRadius: 10,
                    marginBottom: 15,
                  }}
                  onPress={pickImage}
                >
                  <Text style={estilo.textBtn}>Cadastrar um contrato</Text>
                </TouchableOpacity>

                <TouchableOpacity style={estilo.btn} onPress={onSubmit}>
                  <Text style={estilo.textBtn}>Salvar</Text>
                </TouchableOpacity>
              </ScrollView>
            </SafeAreaView>
          </View>
        </Modal>
      </View>
    </View>
  );
}
