import { StyleSheet, Text, View } from "react-native";
import { React, useEffect } from "react";
import { getAuth, onAuthStateChanged } from "firebase/auth";
import estilo from "./css";

export default function CadVeiculo({ navigation }) {

  
  return (
    <View style={estilo.background}>
      <View style={estilo.container}>
        <Text style={estilo.titulo}>
          Cadastre um novo veículo
        </Text>
        <View style={estilo.container3}>
          
          <Text style={estilo.txt2}>
          Nome completo
        </Text>
          <TextInput
            style={estilo.input}
            placeholder="Digite o nome"
            value={nome}
            onChangeText={setNome}
          />
           <Text style={estilo.txt2}>
          Selecione o turno de ida
        </Text>
          <SelectDropdown
            data={Turnos}
            value={form.Ida}
            onSelect={(selectedItem, index) => {
              updateForm({ Ida: selectedItem });
            }}
            defaultButtonText={"Ida"}
            buttonTextAfterSelection={(selectedItem, index) => {
              return selectedItem;
            }}
            rowTextForSelection={(item, index) => {
              return item;
            }}
            renderDropdownIcon={(isOpened) => {
              return (
                <Ionicons
                  name={isOpened ? "chevron-up" : "chevron-down"}
                  color={"#444"}
                  size={18}
                />
              );
            }}
            dropdownIconPosition={"right"}
            dropdownStyle={estilo.DropdownStyle}
            buttonStyle={estilo.drop}
          />
          <Text style={estilo.txt2}>
          Selecione o turno de retorno
        </Text>
          <SelectDropdown
            data={Turnos}
            value={form.Ida}
            onSelect={(selectedItem, index) => {
              updateForm({ Volta: selectedItem });
            }}
            defaultButtonText={"Retorno"}
            buttonTextAfterSelection={(selectedItem, index) => {
              return selectedItem;
            }}
            rowTextForSelection={(item, index) => {
              return item;
            }}
            renderDropdownIcon={(isOpened) => {
              return (
                <Ionicons
                  name={isOpened ? "chevron-up" : "chevron-down"}
                  color={"#444"}
                  size={18}
                />
              );
            }}
            dropdownIconPosition={"right"}
            dropdownStyle={estilo.DropdownStyle}
            buttonStyle={estilo.drop}
          />
          <TouchableOpacity style={estilo.btn} onPress={onSubmit}>
            <Text style={estilo.textBtn}>Enviar</Text>
          </TouchableOpacity>
        </View>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: "#DFDFDF",
  },
  container2: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: "#A4E3F9",
    width: "90%",
    borderRadius: 15,
    borderWidth: 2,
    borderColor: "#004A85",
    marginTop: "1%",
    marginBottom: "5%",
    shadowColor: "black",
    shadowOffset: {
      width: 0,
      height: 12,
    },
    shadowOpacity: 0.58,
    shadowRadius: 16.0,
    elevation: 24,
  },
  cards: {
    flex: 1,
    flexDirection: "row",
    flexWrap: "wrap",
    justifyContent: "center",
    marginTop: "3%",
  },
  card: {
    backgroundColor: "#ffffff",
    alignItems: "center",
    padding: 10,
    maxWidth: "30%",
    maxHeight: 150,
    borderRadius: 20,
    shadowColor: "#000",
    shadowOffset: {
      width: 5,
      height: 5,
    },
    shadowOpacity: 0.75,
    shadowRadius: 5,
    margin: "5%",
    borderRadius: 15,
    borderWidth: 1,
    borderColor: "#004A85",
  },
  icon: {
    textAlign: "center",
    padding: "20%",
  },
});
