import { StyleSheet, Text, View, FlatList,  ScrollView, TouchableOpacity } from "react-native";
import { React, useEffect, useState } from "react";
import { getAuth, onAuthStateChanged } from "firebase/auth";
import {
  collection,
  query,
  where,
  getDocs,
  getFirestore,
  updateDoc
} from "firebase/firestore";
import estilo from "./css";

export default function Usuarios({ navigation }) {
  const [usuario, setUsuario] = useState([]);

  useEffect(() => {
    const auth = getAuth();
    onAuthStateChanged(auth, (user) => {
      if (user) {
        navigation.navigate("Home Administrador");
      } else {
        navigation.navigate("Login");
      }
    });

    usuarios();
  }, []);

  async function usuarios() {
    const db = getFirestore();
    const q = query(collection(db, "Usuarios"), where("Usuario", "!=", " "));
    const querySnapshot = await getDocs(q);
    let a = [];
    querySnapshot.forEach((doc) => {
      const user = {
        id: doc.id,
        usuario: doc.data().Usuario,
        adm: doc.data().Adm,
      };
      a.push(user);
    });
    setUsuario(a);
  }

  const mudaAdm = async (user) => {
    const db = getFirestore();
    const q = query(collection(db, "Usuarios"), where("Usuario", "==", user));
    const querySnapshot = await getDocs(q);
    querySnapshot.forEach((doc) => {
      const user2 = ({
        id: doc.id,
        usuario: doc.data().Usuario,
        adm: doc.data().Adm,
      });      
    });
    await updateDoc(q, {
      Adm: true
  });
  }

  const renderItem = ({ item }) => {
    let adm = false;
    if (item.adm) {
      adm = "Administrador";
    } else {
      adm = "Passageiro";
    }

    return (
      <View style={{ flexDirection: "row" }}>
        <View style={estilo.linha}>
          <Text style={estilo.txt}>Usuário: </Text>
          <Text style={estilo.txt}>{item.usuario}</Text>
        </View>
        <View style={estilo.linha}>
          <Text style={estilo.txt}>Administrador:</Text>
          <Text style={estilo.txt}>{adm}</Text>
        </View>
        <View style={estilo.linha}>
          <TouchableOpacity style={estilo.btn} onPress={() => mudaAdm(item.usuario)}>
            <Text style={estilo.textBtn}>Tornar administrador</Text>
          </TouchableOpacity>
        </View>
      </View>
    );
  };

  return (
    <View style={estilo.background}>
      <View style={estilo.container}>
        <Text style={{ padding: 10, fontWeight: "bold", fontSize: 25 }}>
          Usuários cadastrados no sistema:
        </Text>
        <ScrollView style={{ flex: 1 }} scrollEnabled={false}>
          <FlatList
            style={estilo.flatList}
            data={usuario}
            renderItem={renderItem}
            keyExtractor={(item) => item.id}
          />
        </ScrollView>
      </View>
    </View>
  );
}
