import { StyleSheet, Text, TouchableOpacity } from "react-native";
import { React, useState } from "react";
import { Ionicons } from "@expo/vector-icons";
import { NavigationContainer } from "@react-navigation/native";
import { createStackNavigator } from "@react-navigation/stack";
import { createDrawerNavigator } from "@react-navigation/drawer";
import { createBottomTabNavigator } from "@react-navigation/bottom-tabs";
import { getAuth, signOut } from "firebase/auth";
import { RFPercentage } from "react-native-responsive-fontsize";
import home from "../Home";
import mensalidade from "../consultamensalidade";
import viagem from "../MarcaViagem";
import Login from "../Login";
import CriarLogin from "../CriarLogin";
import HomeAdm from "../HomeAdm";
import Usuarios from "../Usuarios";
import CadVeiculo from "../CadVeiculo";
import Pagamento from "../pagamentos";
import CadPassageiro from "../CadPassageiro";
import ListPassageiro from "../ListPassageiro";

const Stack = createStackNavigator();
const Tab = createBottomTabNavigator();
const Drawer = createDrawerNavigator();

function logout() {
  const auth = getAuth();
  signOut(auth)
    .then(() => {
      navigation.navigate("Login");
    })
    .catch((error) => {
      alert("Erro ao tentar deslogar");
    });
}

function TabNavi() {
  return (
    <Tab.Navigator
      initialRouteName="Home"
      screenOptions={{
        headerShown: false,
        headerRight: () => (
          <TouchableOpacity style={{ margin: 10 }} onPress={() => logout()}>
            <Ionicons name="exit-outline" size={30} color={"white"} />
          </TouchableOpacity>
        ),
        tabBarShowLabel: true,
        tabBarStyle: {
          position: "absolute",
          backgroundColor: "#004A85",
          borderTopWidth: 0,
          bottom: 14,
          left: 10,
          right: 10,
          borderRadius: 10,
        },
      }}
    >
      <Tab.Screen
        name="Home"
        component={home}
        options={{
          headerShown: true,
          headerTitle: "",
          headerStyle: { backgroundColor: "#004A85" },
          tabBarLabelStyle: {
            fontSize: RFPercentage(1.5),
            color: "white",
          },
          tabBarIcon: ({ size, focused }) => {
            if (focused) {
              return <Ionicons name="home" size={size} color={"white"} />;
            }
            return <Ionicons name="home-outline" size={size} color={"white"} />;
          },
        }}
      />
      <Tab.Screen
        name="Consultar Mensalidade"
        component={mensalidade}
        options={{
          headerShown: true,
          headerTitle: "",
          headerStyle: { backgroundColor: "#004A85" },
          tabBarLabelStyle: {
            fontSize: RFPercentage(1.5),
            color: "white",
          },
          tabBarIcon: ({ size, focused }) => {
            if (focused) {
              return (
                <Ionicons name="folder-open" size={size} color={"white"} />
              );
            }
            return (
              <Ionicons
                name="folder-open-outline"
                size={size}
                color={"white"}
              />
            );
          },
        }}
      />
      <Tab.Screen
        name="Efetuar Pagamento"
        component={Pagamento}
        options={{
          headerShown: true,
          headerTitle: "",
          headerStyle: { backgroundColor: "#004A85" },
          tabBarLabelStyle: {
            fontSize: RFPercentage(1.5),
            color: "white",
          },
          tabBarIcon: ({ size, focused }) => {
            if (focused) {
              return <Ionicons name="cash" size={size} color={"white"} />;
            }
            return <Ionicons name="cash-outline" size={size} color={"white"} />;
          },
        }}
      />
      <Tab.Screen
        name="Registar Viagem"
        component={viagem}
        options={{
          headerShown: true,
          headerTitle: "",
          headerStyle: { backgroundColor: "#004A85" },
          tabBarLabelStyle: {
            fontSize: RFPercentage(1.5),
            color: "white",
          },
          tabBarIcon: ({ size, focused }) => {
            if (focused) {
              return <Ionicons name="bus" size={size} color={"white"} />;
            }
            return <Ionicons name="bus-outline" size={size} color={"white"} />;
          },
        }}
      />
    </Tab.Navigator>
  );
}

function DraNavi() {
  const [pass, setPass] = useState(false);
  return (
    <Drawer.Navigator
      useLegacyImplementation
      initialRouteName="Home Administrador"
      screenOptions={{
        drawerStyle: {
          backgroundColor: "#004A85",
        },
      }}
    >
      <Drawer.Screen
        name="Home Administrador"
        component={HomeAdm}
        options={{
          title: "Início",
          headerTitleStyle: { color: "#ffffff" },
          headerStyle: { backgroundColor: "#004A85" },
          headerTintColor: "#fff",
          headerRight: () => (
            <TouchableOpacity style={{ margin: 10 }} onPress={() => logout()}>
              <Ionicons name="exit-outline" size={30} color={"white"} />
            </TouchableOpacity>
          ),
          drawerLabelStyle: {
            fontSize: RFPercentage(1.8),
            color: "white",
          },
          drawerIcon: () => (
            <Ionicons name="home" size={25} color={"#ffffff"} />
          ),
        }}
      />
      <Drawer.Screen
        name="Passageiros"
        component={() => setPass(true)}
        options={{
          title: "Passageiros",
          headerTitleStyle: { color: "#ffffff" },
          headerStyle: { backgroundColor: "#004A85" },
          headerRight: () => (
            <TouchableOpacity style={{ margin: 10 }} onPress={() => logout()}>
              <Ionicons name="exit-outline" size={30} color={"white"} />
            </TouchableOpacity>
          ),
          drawerLabelStyle: {
            fontSize: RFPercentage(1.8),
            color: "white",
          },
          drawerIcon: () => <Ionicons name="person" size={25} color={"#ffffff"} />,
        }}
      />

      {pass && (
        <Drawer.Screen
          name="CadPassageiro"
          component={CadPassageiro}
          options={{
            title: "Cadastro de passageiros",
            headerTitleStyle: { color: "#ffffff" },
            headerStyle: { backgroundColor: "#004A85" },
            headerRight: () => (
              <TouchableOpacity style={{ margin: 10 }} onPress={() => logout()}>
                <Ionicons name="exit-outline" size={30} color={"white"} />
              </TouchableOpacity>
            ),
            drawerLabelStyle: {
              fontSize: RFPercentage(1.8),
              color: "white",
            },
            drawerIcon: () => (
              <Ionicons name="bus" size={25} color={"#ffffff"} />
            ),
          }}
        />,
        <Drawer.Screen
          name="ListPassageiro"
          component={ListPassageiro}
          options={{
            title: "Lista de passageiros",
            headerTitleStyle: { color: "#ffffff" },
            headerStyle: { backgroundColor: "#004A85" },
            headerRight: () => (
              <TouchableOpacity style={{ margin: 10 }} onPress={() => logout()}>
                <Ionicons name="exit-outline" size={30} color={"white"} />
              </TouchableOpacity>
            ),
            drawerLabelStyle: {
              fontSize: RFPercentage(1.8),
              color: "white",
            },
            drawerIcon: () => (
              <Ionicons name="bus" size={25} color={"#ffffff"} />
            ),
          }}
        />
      )}

      <Drawer.Screen
        name="Usuarios"
        component={Usuarios}
        options={{
          title: "Usuários cadastrados",
          headerTitleStyle: { color: "#ffffff" },
          headerStyle: { backgroundColor: "#004A85" },
          headerRight: () => (
            <TouchableOpacity style={{ margin: 10 }} onPress={() => logout()}>
              <Ionicons name="exit-outline" size={30} color={"white"} />
            </TouchableOpacity>
          ),
          drawerLabelStyle: {
            fontSize: RFPercentage(1.8),
            color: "white",
          },
          drawerIcon: () => (
            <Ionicons
              name="person-circle-outline"
              size={25}
              color={"#ffffff"}
            />
          ),
        }}
      />
      <Drawer.Screen
        name="CadVeiculo"
        component={CadVeiculo}
        options={{
          title: "Cadastro de veiculos",
          headerTitleStyle: { color: "#ffffff" },
          headerStyle: { backgroundColor: "#004A85" },
          headerRight: () => (
            <TouchableOpacity style={{ margin: 10 }} onPress={() => logout()}>
              <Ionicons name="exit-outline" size={30} color={"white"} />
            </TouchableOpacity>
          ),
          drawerLabelStyle: {
            fontSize: RFPercentage(1.8),
            color: "white",
          },
          drawerIcon: () => <Ionicons name="bus" size={25} color={"#ffffff"} />,
        }}
      />
    </Drawer.Navigator>
  );
}

export default function StackNavigator() {
  return (
    <NavigationContainer>
      <Stack.Navigator
        initialRouteName="Login"
        screenOptions={{
          headerShown: false,
        }}
      >
        <Stack.Screen name="Login" component={Login} />
        <Stack.Screen name="CriarLogin" component={CriarLogin} />
        <Stack.Screen name="TabNavi" component={TabNavi} />
        <Stack.Screen name="DraNavi" component={DraNavi} />
      </Stack.Navigator>
    </NavigationContainer>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#DFDFDF",
    alignItems: "center",
    justifyContent: "center",
  },
});
