import {
  Text,
  View,
  TextInput,
  TouchableOpacity,
} from "react-native";
import { React, useEffect } from "react";
import estilo from "./css";
import { TextInputMask } from "react-native-masked-text";

export default function ListPassageiro({ navigation }) {  
  const [filtro, setFiltro] = useState("");
  const [passageiros, setPassageiros] = useState("");

  useEffect(() => {
    //passageiro();
  }, []);

  async function passageiro() {
    const db = getFirestore();
    const q = query(collection(db, "Passageiros"), where("Nome", "!=", " "));
    const querySnapshot = await getDocs(q);
    let a = [];
    querySnapshot.forEach((doc) => {
      const x = {
        id: doc.id,
        usuario: doc.data().Usuario,
        adm: doc.data().Adm,
      };
      a.push(x);
    });
    setPassageiros(a);
  }

  return (
    <View style={estilo.background}>
      <View style={estilo.container}>
        <Text style={estilo.titulo}>Usuários cadastrados no sistema:</Text>
        <View
          style={{
            flexDirection: "row",
            justifyContent: "flex-end",
            alignSelf: "flex-end",
            marginRight: "5%",
            marginBottom: "5%"
          }}
        >
          <TextInput
            style={estilo.inputF}
            placeholder="Filtro"
            value={filtro}
            onChangeText={(text) => setFiltro(text)}
          />
          <TouchableOpacity
            style={{
              backgroundColor: "#004A85",
              width: "20%",              
              height: 45,
              alignItems: "center",
              justifyContent: "center",
              borderRadius: 10,
              marginLeft:"2%"
            }}
          >
            <Text style={estilo.textBtn2}>Filtar</Text>
          </TouchableOpacity>
          <TouchableOpacity
            style={{
              backgroundColor: "#004A85",
              width: "auto",
              height: 45,
              alignItems: "center",
              justifyContent: "center",
              borderRadius: 10,
              marginLeft: "2%",
            }}
          >
            <Text style={estilo.textBtn2}>Mostrar tudo</Text>
          </TouchableOpacity>
        </View>
        
      </View>
    </View>
  );
}
