import { StyleSheet, Text, View, Image, Pressable } from "react-native";
import { React, useEffect } from "react";
import { getAuth, onAuthStateChanged } from "firebase/auth";
import estilo from "./css";
import qrcode from "../assets/qrcode";

export default function mensalidades({ navigation }) {
  useEffect(() => {
    const auth = getAuth();
    onAuthStateChanged(auth, (user) => {
      if (user) {
        navigation.navigate("Home");
      } else {
        navigation.navigate("Login");
      }
    });
  }, []);

  return (
    <View style={estilo.background}>
      <View style={estilo.container}>
        <Text style={estilo.titulo}>Suas mensalidades</Text>
        <View style={estilo.container3}>
          <Image source={require("../assets/qrcode.PNG")} />
          <Image
            style={{ width: 50, height: 50 }}
            source={{ uri: "https://facebook.github.io/react/img/logo_og.png" }}
            resizeMode={"cover"} // cover or contain its upto you view look
          />
          <Pressable onPress={onPressFunction}>
            <Text>I'm pressable!</Text>
          </Pressable>
        </View>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: "#DFDFDF",
  },
  container2: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: "#A4E3F9",
    width: "90%",
    borderRadius: 15,
    borderWidth: 2,
    borderColor: "#004A85",
    marginTop: "1%",
    marginBottom: "5%",
    shadowColor: "black",
    shadowOffset: {
      width: 0,
      height: 12,
    },
    shadowOpacity: 0.58,
    shadowRadius: 16.0,
    elevation: 24,
  },
  container3: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: "#DFDFDF",
    width: "90%",
    borderRadius: 15,
    borderWidth: 2,
    borderColor: "#004A85",
    marginTop: "1%",
    marginBottom: "5%",
    shadowColor: "black",
    shadowOffset: {
      width: 0,
      height: 12,
    },
    shadowOpacity: 0.58,
    shadowRadius: 16.0,
    elevation: 24,
  },
});
