import {
  StyleSheet,
  TextInput,
  View,
  KeyboardAvoidingView,
  Image,
  Text,
  TouchableOpacity,
} from "react-native";
import React, { useState, useEffect } from "react";
import { getAuth, createUserWithEmailAndPassword } from "firebase/auth";
import { collection, addDoc } from "firebase/firestore";

export default function CriarLogin({ navigation }) {
  const [email, setEmail] = useState("");
  const [senha, setSenha] = useState("");

  async function cadastrarLogin() {
    const auth = getAuth();
    await createUserWithEmailAndPassword(auth, email, senha).then(value =>{
      try {
        const docRef = addDoc(collection(db, "Usuarios"), {
          Usuario: email,
          Adm: false
        });
        console.log("Document written with ID: ", docRef.id);
      } catch (e) {
        console.error("Error adding document: ", e);
      }
      navigation.navigate("Login");
    }).catch(error => console.log(error));
  }

  return (
    <KeyboardAvoidingView style={styles.background}>
      <View style={styles.container}>
        <View style={styles.Logo}>
          <Text style={{fontSize: 25}}>Cadastre seu email e senha:</Text>
        </View>
        <View style={styles.login}>
          <TextInput
            style={styles.input}
            placeholder="Digite seu e-mail"
            autoCorrect={false}
            value={email}
            onChangeText={setEmail}
          />
          <TextInput
            style={styles.input}
            placeholder="Digite sua senha"
            autoCorrect={false}
            secureTextEntry
            value={senha}
            onChangeText={setSenha}
          />
          <TouchableOpacity
            style={styles.btn}
            title="Entrar"
            onPress={cadastrarLogin}
          >
            <Text style={styles.textBtn}>Cadastrar</Text>
          </TouchableOpacity>
        </View>
      </View>
    </KeyboardAvoidingView>
  );
}

const styles = StyleSheet.create({
  background: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: "#DFDFDF",
  },
  container: {
    flex: 1,
    width: "80%",
    maxHeight: "70%",
    margin: "10%",
    backgroundColor: "#A4E3F9",
    borderRadius: 10,
    borderWidth: 2,
    borderColor: "#004A85",
    shadowColor: "black",
    shadowOffset: {
      width: 0,
      height: 12,
    },
    shadowOpacity: 0.58,
    shadowRadius: 16.0,
    elevation: 24,
  },
  Logo: {
    alignItems: "center",
    justifyContent: "center",
    marginTop: "20%"
  },
  login: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
  },
  input: {
    backgroundColor: "#F5F5F5",
    width: "90%",
    marginBottom: 15,
    color: "#000000",
    fontSize: 17,
    borderRadius: 10,
    padding: 10,
  },
  btn: {
    backgroundColor: "#004A85",
    width: "90%",
    height: 45,
    alignItems: "center",
    justifyContent: "center",
    borderRadius: 10,
  },
  btn2: {
    width: "90%",
    height: 45,
    alignItems: "flex-end",
    justifyContent: "center",
  },
  textBtn: {
    color: "#ffffff",
    fontSize: 18,
  },
});
