import {
  StyleSheet,
  TextInput,
  View,
  KeyboardAvoidingView,
  Image,
  Text,
  TouchableOpacity
} from "react-native";
import React from "react";

export default function Login({ navigation }) {
  return (
    <KeyboardAvoidingView>
      <View>
        <Image source={require('../assets/logo.png')}/>
      </View>
      <View>
        <TextInput 
        placeholder="Digite seu Login"
        autoCorrect={false}
        onChange={() => {}}
        />
        <TextInput 
        placeholder="Digite sua senha"
        autoCorrect={false}
        onChange={() => {}}
        />
        <TouchableOpacity title="Entrar" onPress={() => navigation.navigate("Home")}>
          <Text>Acessar</Text>  
        </TouchableOpacity>  
      </View>
    </KeyboardAvoidingView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
  },
});
