import {
  StyleSheet,
  Text,
  View,
  TouchableOpacity,
  TextInput,
} from "react-native";
import { Card } from "react-native-paper";
import { React, useState } from "react";
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';

const Tab = createBottomTabNavigator();

export default function Home() {
  return (
    <NavigationContainer style={styles.container}>
      <Stack.Navigator initialRouteName="Login">
        <Stack.Screen
          options={{
            title: "",
            headerhBackVisible: true,
          }}
          name="Home"
          component={Home}
        />
        <Stack.Screen
          options={{
            title: "",
            headerTransparent: true,
            headerShown: false

          }}
          name="Login"
          component={Login}
        />
      </Stack.Navigator>
    </NavigationContainer>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: "#DFDFDF",
  },
  container2: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: "#A4E3F9",
    width: "90%",
    maxHeight: "95%",
    borderRadius: 15,
    borderWidth: 1,
    borderColor: "#004A85",
  },
  card: {
    flex: 1,
    backgroundColor: "#DFDFDF",
    width: "90%",
    maxHeight: "98%",
    borderRadius: 15,
    borderWidth: 1,
    borderColor: "#004A85",
    padding: 15,
  },
  form: {
    marginTop: 20,
  },
  input: {
    backgroundColor: "#FFFFFF",
    borderRadius: 15,
    borderWidth: 1,
    borderColor: "#004A85",
    padding: 5,
    paddingLeft: 10,
    width: "50%",
  },
});
