import {
  Text,
  View,
  FlatList,
  Pressable,
  TextInput,
  TextComponent,
  TouchableOpacity,
} from "react-native";
import { Card } from "react-native-paper";
import { React, useEffect, useState } from "react";
import {
  collection,
  query,
  where,
  getDocs,
  getFirestore,
  deleteDoc,
  doc,
} from "firebase/firestore";
import estilo from "./css";
import { Ionicons } from "@expo/vector-icons";
import { TextInputMask } from "react-native-masked-text";

export default function Viagens({ navigation }) {
  const [viagem, setViagem] = useState([]);
  const [filtro, setFiltro] = useState(new Date());

  useEffect(() => {
    viagens();
  }, []);

  async function viagens() {
    const db = getFirestore();
    const q = query(collection(db, "Viagens"));
    const snapshot = await getDocs(q);
    const data = snapshot.docs.map((doc) => ({
      ...doc.data(),
      id: doc.id,
    }));
    data.map(async (elem) => {
      const workQ = query(collection(db, `Viagens/${JSON.stringify("12.12.2022")}/Nomes`));
      const workDetails = await getDocs(workQ);
      let a = [];
      const workInfo = workDetails.docs.map((doc) => {
        const x = {
          id: doc.id,
          Nome: doc.data().Nome,
          Data: doc.data().Data,
          Ida: doc.data().Ida,
          Volta: doc.data().Volta,
        };
        a.push(x);
      });
      setViagem(a);
    });
  }

  const excluirUsu = async (user) => {
    const db = getFirestore();
    const q = query(collection(db, "Usuarios"), where("Usuario", "==", user));
    const querySnapshot = await getDocs(q);
    let id = "";
    querySnapshot.forEach((doc) => {
      id = doc.id;
    });
    await deleteDoc(doc(db, "Usuarios", id));
    viagens();
    alert("Usuário excluído!");
  };

  const renderItem = ({ item }) => {
    return (
      <View
        style={{
          flexDirection: "row",
          alignItems: "center",
          justifyContent: "center",
          width: "100%",
          marginBottom: "1%",
        }}
      >
        <Card style={estilo.card}>
          <Card.Content style={estilo.coluna}>
            <View style={estilo.linha}>
              <Text style={estilo.txtFlat}>Nome:</Text>
              <Text style={estilo.info}>{item.Nome}</Text>
            </View>
            <View style={estilo.linha}>
              <Text style={estilo.txtFlat}>Data:</Text>
              <Text style={estilo.info}>{item.Data}</Text>
            </View>
          </Card.Content>
          <Card.Actions style={estilo.cardAct}>
            <Pressable
              style={estilo.btncard}
              onPress={() => mudaAdm(item.Nome)}
            >
              <Text style={estilo.textBtn2}>Mudar Permissão</Text>
            </Pressable>
            <Pressable
              style={estilo.btncard}
              onPress={() => excluirUsu(item.Nome)}
            >
              <Ionicons name="trash-outline" size={25} color={"white"} />
            </Pressable>
          </Card.Actions>
        </Card>
      </View>
    );
  };

  function filtrado() {
    let filt = viagem.filter(
      (item) => item.viagem.toLowerCase().indexOf(filtro.toLowerCase()) > -1
    );
    setViagem(filt);
  }

  return (
    <View style={estilo.background}>
      <View style={estilo.container}>
        <Text style={estilo.titulo}>Viagens</Text>
        <View
          style={{
            flexDirection: "row",
            justifyContent: "flex-end",
            alignSelf: "flex-end",
            marginRight: "5%",
            marginBottom: "5%",
          }}
        >
           <TextInputMask
            style={estilo.inputF}
            placeholder="Digite uma data"
            type={"custom"}
            options={{
              mask: "99.99.9999",
            }}
            value={filtro}
            onChangeText={(text) => setFiltro(text)}
            keyboardType="numeric"
            maxLength={10}
          />
          <TouchableOpacity
            style={{
              backgroundColor: "#004A85",
              width: "20%",
              height: 45,
              alignItems: "center",
              justifyContent: "center",
              borderRadius: 10,
              marginLeft: "2%",
            }}
            onPress={() => filtrado()}
          >
            <Text style={estilo.textBtn2}>Filtar</Text>
          </TouchableOpacity>
          <TouchableOpacity
            style={{
              backgroundColor: "#004A85",
              width: "auto",
              height: 45,
              alignItems: "center",
              justifyContent: "center",
              borderRadius: 10,
              marginLeft: "2%",
            }}
            onPress={() => usuarios()}
          >
            <Text style={estilo.textBtn2}>Mostrar tudo</Text>
          </TouchableOpacity>
        </View>
        <FlatList
          style={estilo.flatList}
          data={viagem}
          renderItem={renderItem}
          keyExtractor={(item) => item.id}
        />
      </View>
    </View>
  );
}
