import {
  Text,
  View,
  FlatList,
  Pressable,
  TextInput,
  TouchableOpacity,
  Modal,
  Button,
  StyleSheet,
  ScrollView,
  SafeAreaView,
} from "react-native";
import { Card } from "react-native-paper";
import { React, useEffect, useState } from "react";
import {
  collection,
  query,
  where,
  getDocs,
  getFirestore,
  deleteDoc,
  setDoc,
  doc,
} from "firebase/firestore";
import { TextInputMask } from "react-native-masked-text";
import SelectDropdown from "react-native-select-dropdown";
import { Ionicons } from "@expo/vector-icons";
import estilo from "./css";
import { RFPercentage } from "react-native-responsive-fontsize";

export default function ListMensalidade({ navigation }) {  
  const meses = ["Jan", "Fev", "Mar","Abr", "Mai", "Jun", "Jul", "Ago", "Set", "Out", "Nov", "Dez"];
  const [inputs, setInputs] = useState([{ mes: "", valor: "" }]);
  const [instituicao, setInstituicao] = useState([]);
  const [mensalidade, setMensalidade] = useState([]);
  const [filtro, setFiltro] = useState("");
  const [submit, setSubmit] = useState("");
  const [visible, setModalVisible] = useState(false);
  const [form, setForm] = useState({
    Modelo: "",
    Consumo: "",
    Capacidade: "",
    Fabricacao: "",
    Status: "",
    Regularizacao: "",
  });

  function updateForm(value) {
    return setForm((prev) => {
      return { ...prev, ...value };
    });
  }

  useEffect(() => {
    instituicoes();
    mensalidades();
  }, []);

  async function instituicoes() {
    const db = getFirestore();
    const q = query(
      collection(db, "Instituicoes"),
      where("NomeInst", "!=", " ")
    );
    const querySnapshot = await getDocs(q);
    let a = [];
    querySnapshot.forEach((doc) => {
      a.push(doc.data().NomeInst);
    });
    setInstituicao(a);
  }

  async function mensalidades() {
    const db = getFirestore();
    const q = query(collection(db, "Mensalidades"));
    const querySnapshot = await getDocs(q);
    let a = [];
    querySnapshot.forEach((doc) => {
      const x = {
        id: doc.id,
      };
      a.push(x);
    });
    setMensalidade(a);
  }

  const excluir = async (Modelo) => {
    const db = getFirestore();
    const q = query(collection(db, "Veiculos"), where("Modelo", "==", Modelo));
    const querySnapshot = await getDocs(q);
    let id = "";
    querySnapshot.forEach((doc) => {
      id = doc.data().Modelo;
    });
    await deleteDoc(doc(db, "Veiculos", id));
    veiculos();
    alert("Veículo excluído!");
  };

  const edit = async (item) => {
    setSubmit("Edição");
    setNome(item.Modelo);
    setModalVisible(!visible);
    setForm({
      Modelo: item.Modelo,
      Consumo: item.Consumo,
      Capacidade: item.Capacidade,
      Fabricacao: item.Fabricacao,
      Status: item.Status,
      Regularizacao: item.Regularizacao,
    });
  };

  const add = async () => {
    setSubmit("Cadastro");
    setModalVisible(!visible);
  };

  const renderItem = ({ item }) => {
    return (
      <View
        style={{
          flexDirection: "row",
          alignItems: "center",
          justifyContent: "center",
          width: "100%",
          marginBottom: "1%",
        }}
      >
        <Card style={estilo.card}>
          <Card.Content style={estilo.coluna}>
            <View style={estilo.linha}>
              <Text style={estilo.txtFlat}>Modelo:</Text>
              <Text style={estilo.info}>{item.Modelo}</Text>
            </View>
            <View style={estilo.linha}>
              <Text style={estilo.txtFlat}>Consumo:</Text>
              <Text style={estilo.info}>{item.Consumo}</Text>
            </View>
            <View style={estilo.linha}>
              <Text style={estilo.txtFlat}>Capacidade:</Text>
              <Text style={estilo.info}>{item.Capacidade}</Text>
            </View>
            <View style={estilo.linha}>
              <Text style={estilo.txtFlat}>Fabricacao:</Text>
              <Text style={estilo.info}>{item.Fabricacao}</Text>
            </View>
            <View style={estilo.linha}>
              <Text style={estilo.txtFlat}>Status:</Text>
              <Text style={estilo.info}>{item.Status}</Text>
            </View>
            <View style={estilo.linha}>
              <Text style={estilo.txtFlat}>Regularização:</Text>
              <Text style={estilo.info}>{item.Regularizacao}</Text>
            </View>
          </Card.Content>
          <Card.Actions style={estilo.cardAct}>
            <Pressable style={estilo.btncard} onPress={() => edit(item)}>
              <Ionicons name="pencil-outline" size={25} color={"white"} />
            </Pressable>
            <Pressable
              style={estilo.btncard}
              onPress={() => excluir(item.Modelo)}
            >
              <Ionicons name="trash-outline" size={25} color={"white"} />
            </Pressable>
          </Card.Actions>
        </Card>
      </View>
    );
  };

  function filtrado() {
    let filt = veiculo.filter(
      (item) => item.Modelo.toLowerCase().indexOf(filtro.toLowerCase()) > -1
    );
    setVeiculo(filt);
  }

  const addHandler = () => {
    const _inputs = [...inputs];
    _inputs.push({ mes: "", valor: "" });
    setInputs(_inputs);
  };

  const deleteHandler = (key) => {
    const _inputs = inputs.filter((input, index) => index != key);
    setInputs(_inputs);
  };

  const inputHandler = (text, key) => {
    const _inputs = [...inputs];
    _inputs[key].valor = text;
    _inputs[key].mes = key;
    setInputs(_inputs);
  };

  async function onSubmit() {
    /*if (submit == "Edição") {
      const db = getFirestore();
      await deleteDoc(doc(db, "Veiculos", nome));
      await setDoc(doc(db, "Veiculos", form.Modelo), {
        Modelo: form.Modelo,
        Consumo: form.Consumo,
        Capacidade: form.Capacidade,
        Fabricacao: form.Fabricacao,
        Status: form.Status,
        Regularizacao: form.Regularizacao,
      });
      alert("Edição realizada!");
    } else if (submit == "Cadastro") {
      const db = getFirestore();
      await setDoc(doc(db, "Veiculos", form.Modelo), {
        Modelo: form.Modelo,
        Consumo: form.Consumo,
        Capacidade: form.Capacidade,
        Fabricacao: form.Fabricacao,
        Status: form.Status,
        Regularizacao: form.Regularizacao,
      });
      alert("Cadastro realizado!");
    }
    setModalVisible(!visible);
    setForm({
      Modelo: "",
      Consumo: "",
      Capacidade: "",
      Fabricacao: "",
      Status: "",
      Regularizacao: "",
    });
    veiculos();*/
  }

  return (
    <View style={estilo.background}>
      <View style={estilo.container}>
        <Text style={estilo.titulo2}>Mensalidades</Text>
        <View
          style={{
            flexDirection: "row",
            justifyContent: "center",
            flexWrap: "wrap",
            width: "90%",
            marginBottom: "2%",
          }}
        >
          <TouchableOpacity
            style={{
              flexDirection: "row",
              backgroundColor: "#004A85",
              width: "auto",
              height: 45,
              alignItems: "center",
              justifyContent: "center",
              borderRadius: 10,
              padding: 2,
              marginRight: "10%",
            }}
            onPress={() => add()}
          >
            <Ionicons name="add-outline" size={15} color={"white"} />
            <Text style={estilo.textBtn2}>Adicionar mensalidade</Text>
          </TouchableOpacity>

          <TextInput
            style={estilo.inputF}
            placeholder="Filtro"
            value={filtro}
            onChangeText={(text) => setFiltro(text)}
          />
          <TouchableOpacity
            style={{
              backgroundColor: "#004A85",
              width: "auto",
              height: 45,
              alignItems: "center",
              justifyContent: "center",
              borderRadius: 10,
              marginLeft: "0.5%",
              padding: "1%",
            }}
            onPress={() => filtrado()}
          >
            <Ionicons name="search-outline" size={25} color={"white"} />
          </TouchableOpacity>
          <TouchableOpacity
            style={{
              backgroundColor: "#004A85",
              width: "auto",
              height: 45,
              alignItems: "center",
              justifyContent: "center",
              borderRadius: 10,
              marginLeft: "1.5%",
            }}
            onPress={() => veiculos()}
          >
            <Text style={estilo.textBtn2}>Mostrar tudo</Text>
          </TouchableOpacity>
        </View>
        <FlatList
          style={estilo.flatList}
          data={mensalidade}
          renderItem={renderItem}
          keyExtractor={(item) => item.id}
        />
        <Modal
          animationType="slide"
          transparent={true}
          visible={visible}
          onRequestClose={() => {
            setModalVisible(!visible);
          }}
        >
          <View style={estilo.modal}>
            <Ionicons
              name="close-circle-outline"
              size={25}
              color={"black"}
              onPress={() => {
                setModalVisible(!visible);
                setForm({
                  Modelo: "",
                  Consumo: "",
                  Capacidade: "",
                  Fabricacao: "",
                  Status: "",
                  Regularizacao: "",
                });
              }}
              style={{ alignSelf: "flex-end", marginTop: 15 }}
            />

            <Text style={estilo.titulo2}>{submit + " dos dados"}</Text>
            <SafeAreaView style={{ width: "100%", height: "90%" }}>
              <ScrollView contentContainerStyle={estilo.ScrollView}>
                  {inputs.map((input, key) => (
                    <View style={{flexDirection: "row", justifyContent: "center", alignItems: "center"}} key={key}>
                      <SelectDropdown
                        data={meses}
                        value={input.mes}
                        onSelect={(selectedItem, index) => {
                          console.log(selectedItem)
                        }}
                        defaultButtonText={"Mês"}
                        buttonTextAfterSelection={(selectedItem, index) => {
                          return selectedItem;
                        }}
                        rowTextForSelection={(item, index) => {
                          return item;
                        }}
                        renderDropdownIcon={(isOpened) => {
                          return (
                            <Ionicons
                              name={isOpened ? "chevron-up" : "chevron-down"}
                              color={"#444"}
                              size={18}
                            />
                          );
                        }}
                        dropdownIconPosition={"right"}
                        dropdownStyle={style.DropdownStyle}
                        buttonStyle={style.drop}
                      />
                      <TextInputMask
                        style={estilo.input}
                        type={"money"}
                        placeholder={"Digite o valor"}
                        value={input.valor}
                        onChangeText={(text) => inputHandler(text, key)}
                      />

                      <TouchableOpacity onPress={() => deleteHandler(key)}>
                        <Ionicons
                          name="trash-outline"
                          size={15}
                          color={"white"}
                        />
                      </TouchableOpacity>
                    </View>
                  ))}
                <Button title="Adicionar mês" onPress={addHandler} />
                <TouchableOpacity style={estilo.btn} onPress={onSubmit}>
                  <Text style={estilo.textBtn}>Salvar</Text>
                </TouchableOpacity>
              </ScrollView>
            </SafeAreaView>
          </View>
        </Modal>
      </View>
    </View>
  );
}

const style = StyleSheet.create({
  DropdownStyle: {
    backgroundColor: "#ffffff",
    height: "auto",
    color: "#000000",
    fontSize: RFPercentage(1.5),
    borderRadius: 10,
    borderColor: "#004A85",
    borderWidth: 1,
  },
  drop: {
    backgroundColor: "#F5F5F5",
    height: 45,
    width: "40%",
    color: "#000000",
    fontSize: RFPercentage(1.5),
    borderRadius: 10,
    borderColor: "#004A85",
    borderWidth: 1,
    shadowColor: "black",
    shadowOffset: {
      width: 0,
      height: 6,
    },
    shadowOpacity: 0.37,
    shadowRadius: 7.49,
    elevation: 12,
    marginLeft: "3%",
  },
  titulo: {
    padding: 10,
    textAlign: "left",
    fontSize: RFPercentage(2),
  },
});
