import { StyleSheet, Text, View } from "react-native";
import React from "react";
import { NavigationContainer } from "@react-navigation/native";
import { createBottomTabNavigator } from "@react-navigation/bottom-tabs";
import { createStackNavigator } from '@react-navigation/stack';
import home from "./Home";
import mensalidade from "./consultamensalidade";
import viagem from "./listaViagem";
import { Ionicons } from "@expo/vector-icons";
import Login from "./Login";

const Tab = createBottomTabNavigator();
const Stack = createStackNavigator();

export default function Rotas() {
  return (
    <NavigationContainer style={styles.container}>
      <Tab.Navigator
        initialRouteName={Login}
        screenOptions={{
          tabBarShowLabel: true,
          tabBarStyle: {
            position: "absolute",
            backgroundColor: "#004A85",
            borderTopWidth: 0,
            bottom: 14,
            left: 10,
            right: 10,
            borderRadius: 10,
          },
        }}
      >
        <Stack.Screen
          name="Login"
          component={Login}
          options={{ headerShown: false, tabBarShowLabel: false }}
        />
        <Tab.Screen
          name="Início"
          component={home}
          options={{
            headerShown: true,
            headerTitle: "",
            headerStyle: { backgroundColor: "#004A85" },
            tabBarIcon: ({ size, focused }) => {
              if (focused) {
                return <Ionicons name="home" size={size} color={"white"} />;
              }
              return (
                <Ionicons name="home-outline" size={size} color={"white"} />
              );
            },
          }}
        />
        <Tab.Screen
          name="Consultar Mensalidade"
          component={mensalidade}
          options={{
            headerShown: true,
            headerTitle: "",
            headerStyle: { backgroundColor: "#004A85" },
            tabBarIcon: ({ size, focused }) => {
              if (focused) {
                return (
                  <Ionicons name="folder-open" size={size} color={"white"} />
                );
              }
              return (
                <Ionicons
                  name="folder-open-outline"
                  size={size}
                  color={"white"}
                />
              );
            },
          }}
        />
        <Tab.Screen
          name="Registar Viagem"
          component={viagem}
          options={{
            headerShown: true,
            headerTitle: "",
            headerStyle: { backgroundColor: "#004A85" },
            tabBarIcon: ({ size, focused }) => {
              if (focused) {
                return <Ionicons name="bus" size={size} color={"white"} />;
              }
              return (
                <Ionicons name="bus-outline" size={size} color={"white"} />
              );
            },
          }}
        />
      </Tab.Navigator>
    </NavigationContainer>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#DFDFDF",
    alignItems: "center",
    justifyContent: "center",
  },
});
