import { StyleSheet } from "react-native";
import { RFPercentage, RFValue } from "react-native-responsive-fontsize";

export default StyleSheet.create({
  background: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: "#DFDFDF",
  },
  container: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: "#c8f1ff",
    width: "90%",
    borderRadius: 15,
    borderWidth: 2,
    borderColor: "#004A85",
    marginTop: "1%",
    marginBottom: "5%",
    shadowColor: "black",
    shadowOffset: {
      width: 0,
      height: 12,
    },
    shadowOpacity: 0.58,
    shadowRadius: 16.0,
    elevation: 24,
  },
  Logo: {
    alignItems: "center",
    justifyContent: "center",
    marginTop: "10%",
  },
  login: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
  },
  input: {
    backgroundColor: "#F5F5F5",
    width: "90%",
    marginBottom: 15,
    color: "#000000",
    fontSize: 17,
    borderRadius: 10,
    borderWidth: 0.5,
    padding: 10,
    borderColor: "#004A85",
    shadowColor: "black",
    shadowOffset: {
      width: 0,
      height: 5,
    },
    shadowOpacity: 0.58,
    shadowRadius: 5,
    elevation: 10,
  },
  btn: {
    backgroundColor: "#004A85",
    width: "90%",
    height: 45,
    alignItems: "center",
    justifyContent: "center",
    borderRadius: 10,
  },
  btn2: {
    width: "90%",
    height: 45,
    alignItems: "center",
    justifyContent: "center",
  },
  txt: {
    fontSize: RFPercentage(2),
    textAlign: "center",
    alignSelf: "center",
  },
  textBtn: {
    color: "#ffffff",
    fontSize: 18, 
    marginLeft:10
  },
  linha: {
    flexDirection: "row",
    width: "100%",
  },
  coluna:{
    flexDirection: "column",
    alignSelf: "center",
    width: "100%",
  },
  cardAct:{
    alignSelf: "center",
    justifyContent: "center",
    borderRadius: 10,
    backgroundColor: '#004A85',
  },
  btncard: {
    alignItems: 'center',
    justifyContent: 'center',
    paddingVertical: 12,
    paddingHorizontal: 32,
    borderRadius: 4,
    elevation: 3,
  },
  textBtn2: {
    color: "#ffffff",
    fontSize: 15, 
    padding: 5
  },
  card: {
    alignItems: "center",
    backgroundColor: "#ffffff",
    borderRadius: 15,
    borderWidth: 2,
    borderColor: "#004A85",
    padding: 5,
    width: "100%"
  },
  flatList:{
    width: "90%"
  }
});
