import { StyleSheet, Text, View } from "react-native";
import React from "react";
import { NavigationContainer } from "@react-navigation/native";
import { createBottomTabNavigator } from "@react-navigation/bottom-tabs";
import home from "./components/Home";
import mensalidade from "./components/consultamensalidade";
import viagem from './components/listaViagem';
import {Ionicons} from '@expo/vector-icons';

const Tab = createBottomTabNavigator();

export default function App() {
  return (
    <NavigationContainer style={styles.container}>
      <Tab.Navigator
        initialRouteName={"Home"}
        screenOptions={{ tabBarShowLabel: true, tabBarStyle:{
          position: 'absolute',
          backgroundColor: "#004A85",
          borderTopWidth:0,
          bottom: 14,
          left: 20,
          right: 20,
          borderRadius: 10
        } }}
      >
        <Tab.Screen name="Início" component={home} options={{
          headerShown: false,
          tabBarIcon:({color, size, focused}) => {
            if(focused){
              return <Ionicons name="home" size={size} color={color}/>
            }
            return <Ionicons name="home-outline" size={size} color={color}/>
          }
        }}/>
        <Tab.Screen name="Consultar Mensalidade" component={mensalidade} options={{
          headerShown: false,
          tabBarIcon:({color, size, focused}) => {
            if(focused){
              return <Ionicons name="folder-open" size={size} color={"red"}/>
            }
            return <Ionicons name="folder-open-outline" size={size} color={color}/>
          }
        }}/>
        <Tab.Screen name="Registar Viagem" component={viagem} options={{
          headerShown: false,
          tabBarIcon:({color, size, focused}) => {
            if(focused){
              return <Ionicons name="bus" size={size} color={color}/>
            }
            return <Ionicons name="bus-outline" size={size} color={color}/>
          }
        }}/>
      </Tab.Navigator>
    </NavigationContainer>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#DFDFDF",
    alignItems: "center",
    justifyContent: "center",
  },
});
