import { StyleSheet, Text, View, FlatList } from "react-native";
import { React, useEffect, useState } from "react";
import { getAuth, onAuthStateChanged } from "firebase/auth";
import {
  collection,
  query,
  where,
  getDocs,
  getFirestore,
} from "firebase/firestore";

export default function Usuarios({ navigation }) {
  const [usuario, setUsuario] = useState([]);

  useEffect(() => {
    const auth = getAuth();
    onAuthStateChanged(auth, (user) => {
      if (user) {
        navigation.navigate("Home Administrador");
      } else {
        navigation.navigate("Login");
      }
    });

    usuarios();
  }, []);

  async function usuarios() {
    const db = getFirestore();
    const q = query(collection(db, "Usuarios"), where("Usuario", "!=", " "));
    const querySnapshot = await getDocs(q);
    let a = [];
    querySnapshot.forEach((doc) => {
      const user = {
        id: doc.id,
        usuario: doc.data().Usuario,
        adm: doc.data().Adm,
      };
      a.push(user);
    });
    setUsuario(a);
  }

  
  const renderItem = ({ item }) => {
    let adm = false;
      if(item.adm){
        adm = "Administrador"
      }else{
        adm = "Passageiro"
      }
    return(      
      <View style={{ flexDirection: "row" }}>
      <View style={{flexDirection: "row", width:"50", borderWidth:1, borderColor:"#A4E3F9" }}>
        <Text style={{ fontSize: 15}}>{item.usuario}</Text>
      </View>
      <View style={{flexDirection: "row", width:"50", borderWidth:1, borderColor:"#A4E3F9" }}>
        <Text style={{ fontSize: 15}}>{adm}</Text>
      </View>
    </View>
    )
  };

  return (
    <View style={styles.container}>
      <View style={styles.container2}>
        <Text style={{ padding: 10, fontWeight: "bold", fontSize: 25 }}>
          Usuários cadastrados no sistema:
        </Text>
        <View style={{ flexDirection: "rowe" }}>
        <Text style={{ fontSize: 20 }}>Usuário: </Text>
        <Text style={{ fontSize: 20 }}>Administrador:</Text>
    </View>
        <FlatList
          style={styles.flatList}
          data={usuario}
          renderItem={renderItem}
          keyExtractor={(item) => item.id}
        />
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: "#DFDFDF",
  },
  container2: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: "#A4E3F9",
    width: "90%",
    borderRadius: 15,
    borderWidth: 2,
    borderColor: "#004A85",
    marginTop: "1%",
    marginBottom: "5%",
    shadowColor: "black",
    shadowOffset: {
      width: 0,
      height: 12,
    },
    shadowOpacity: 0.58,
    shadowRadius: 16.0,
    elevation: 24,
  },
  cards: {
    flex: 1,
    flexDirection: "row",
    flexWrap: "wrap",
    justifyContent: "center",
    marginTop: "3%",
  },
  card: {
    backgroundColor: "#ffffff",
    alignItems: "center",
    padding: 10,
    maxWidth: "30%",
    maxHeight: 150,
    borderRadius: 20,
    shadowColor: "#000",
    shadowOffset: {
      width: 5,
      height: 5,
    },
    shadowOpacity: 0.75,
    shadowRadius: 5,
    margin: "5%",
    borderRadius: 15,
    borderWidth: 1,
    borderColor: "#004A85",
  },
  icon: {
    textAlign: "center",
    padding: "20%",
  },
});
