import {
  StyleSheet,
  Text,
  View,
  FlatList,
  TouchableOpacity,
} from "react-native";
import {Card, Title, Paragraph } from 'react-native-paper';
import { React, useEffect, useState } from "react";
import { getAuth, onAuthStateChanged, deleteUser } from "firebase/auth";
import {
  collection,
  query,
  where,
  getDocs,
  getFirestore,
  deleteDoc,
  updateDoc,
  doc,
} from "firebase/firestore";
import estilo from "./css";

export default function Usuarios({ navigation }) {
  const [usuario, setUsuario] = useState([]);

  useEffect(() => {
    const auth = getAuth();
    onAuthStateChanged(auth, (user) => {
      if (user) {
        navigation.navigate("Home Administrador");
      } else {
        navigation.navigate("Login");
      }
    });

    usuarios();
  }, []);

  async function usuarios() {
    const db = getFirestore();
    const q = query(collection(db, "Usuarios"), where("Usuario", "!=", " "));
    const querySnapshot = await getDocs(q);
    let a = [];
    querySnapshot.forEach((doc) => {
      const user = {
        id: doc.id,
        usuario: doc.data().Usuario,
        adm: doc.data().Adm,
      };
      a.push(user);
    });
    setUsuario(a);
  }

  const mudaAdm = async (user) => {
    const db = getFirestore();
    const q = query(collection(db, "Usuarios"), where("Usuario", "==", user));
    const querySnapshot = await getDocs(q);
    let id,
      permissao = "";
    querySnapshot.forEach((doc) => {
      (id = doc.id), (permissao = doc.data().Adm);
    });
    const use = doc(db, "Usuarios", id);
    await updateDoc(use, {
      Adm: !permissao,
    });
    alert("Permissão do usuário alterada");
  };

  const excluirAdm = async (user) => {
    const db = getFirestore();
    const q = query(collection(db, "Usuarios"), where("Usuario", "==", user));
    const querySnapshot = await getDocs(q);
    let id = "";
    querySnapshot.forEach((doc) => {
      id = doc.id;
    });
    await deleteDoc(doc(db, "Usuarios", id));
    alert("Usuário excluído!");
  };

  const renderItem = ({ item }) => {
    let adm = false;
    if (item.adm) {
      adm = "Administrador";
    } else {
      adm = "Passageiro";
    }

    return (
      <View style={{ flexDirection: "row" }}>
        <View style={estilo.linha}>
          <Text style={estilo.txt}>Usuário: </Text>
          <Text style={estilo.txt}>{item.usuario}</Text>
        </View>
        <View style={estilo.linha}>
          <Text style={estilo.txt}>Administrador:</Text>
          <Text style={estilo.txt}>{adm}</Text>
        </View>
        <View style={estilo.linha}></View>

        <Card>
          <Card.Title
            title="Card Title"
            subtitle="Card Subtitle"
            left={LeftContent}
          />
          <Card.Content>
            <Title>Card title</Title>
            <Paragraph>Card content</Paragraph>
          </Card.Content>
          <Card.Actions>
            <TouchableOpacity
              style={estilo.btn}
              onPress={() => mudaAdm(item.usuario)}
            >
              <Text style={estilo.textBtn}>Mudar Permissão</Text>
            </TouchableOpacity>
            <TouchableOpacity
              style={estilo.btn}
              onPress={() => excluirAdm(item.usuario)}
            >
              <Text style={estilo.textBtn}>Excluir Usuário</Text>
            </TouchableOpacity>
          </Card.Actions>
        </Card>
      </View>
    );
  };

  return (
    <View style={estilo.background}>
      <View style={estilo.container}>
        <Text style={{ padding: 10, fontWeight: "bold", fontSize: 25 }}>
          Usuários cadastrados no sistema:
        </Text>
        <FlatList
          style={estilo.flatList}
          data={usuario}
          renderItem={renderItem}
          keyExtractor={(item) => item.id}
        />
      </View>
    </View>
  );
}
