import { StyleSheet, Text, View } from "react-native";
import { Card } from "react-native-paper";
import React from "react";

export default function Home() {  
  return (
    <View style={styles.container}>
      <View style={styles.container2}>
        <Card style={styles.card}>
          <Text style={{fontSize: 30}}>Seja bem-vindo(a) ao sistema Turis Info!</Text>
          <Text style={{fontSize: 20}}>Quais opções voce deseja visitar?</Text>
        </Card>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: "#DFDFDF",
  },
  container2: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: "#A4E3F9",
    width: "90%",
    maxHeight: "95%",
    borderRadius: 15,
    borderWidth: 1,
    borderColor: "#004A85",
  },
  card:{
    flex:1,
    backgroundColor: '#DFDFDF',
    width: "90%",
    maxHeight: "98%",
    borderRadius: 15,
    borderWidth: 1,
    borderColor: "#004A85",
    padding: 15
  }
});
