import {
  StyleSheet,
  Text,
  View,
  TouchableOpacity,
  TextInput,
} from "react-native";
import { React, useState } from "react";
import SelectDropdown from "react-native-select-dropdown";
import {Ionicons} from '@expo/vector-icons';

export default function listaViagem() {
  const Turnos = ["Manhã", "Tarde", "Noite", "Não"];
  const [form, setForm] = useState({
    Data: "",
    Nome: "",
    Ida: "",
    Volta: "",
  });

  function updateForm(value) {
    return setForm((prev) => {
      return { ...prev, ...value };
    });
  }
  async function onSubmit(e) {
    e.preventDefault();

    const newV = { ...form };

    await fetch("http://localhost:5000/record/addViagem", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(newV),
    }).catch((error) => {
      window.alert(error);
    });

    setForm({
      Data: "",
      Nome: "",
      Ida: "",
      Volta: "",
    });
  }
  return (
    <View style={styles.container}>
      <View style={styles.container2}>
        <Text style={{ padding: 10, fontWeight: "bold", fontSize: 20 }}>
          Registre o horário de sua viagem
        </Text>
        <View style={styles.container3}>
          <TextInput
            style={styles.input}
            placeholder="Digite uma data"
            autoCorrect={false}
            value={form.Data}
            onChange={(e) => updateForm({ Data: e.target.value })}
          />
          <TextInput
            style={styles.input}
            placeholder="Digite seu nome completo"
            autoCorrect={false}
            value={form.Nome}
            onChange={(e) => updateForm({ Nome: e.target.value })}
          />
          <SelectDropdown
            data={Turnos}            
            value={form.Ida}
            onSelect={(selectedItem, index) => {
              updateForm({ Ida: selectedItem });
            }}
            defaultButtonText={"Selecione o turno de ida"}
            buttonTextAfterSelection={(selectedItem, index) => {
              return selectedItem;
            }}
            rowTextForSelection={(item, index) => {
              return item;
            }}
            renderDropdownIcon={(isOpened) => {
              return (
                <Ionicons
                  name={isOpened ? "chevron-up" : "chevron-down"}
                  color={"#444"}
                  size={18}
                />
              );
            }}
            dropdownIconPosition={"right"}
          />

          <TextInput
            style={styles.input}
            placeholder="Turno de volta"
            autoCorrect={false}
            value={form.Volta}
            onChange={(e) => updateForm({ Volta: e.target.value })}
          />
          <TouchableOpacity style={styles.btn} onPress={onSubmit}>
            <Text style={styles.textBtn}>Enviar</Text>
          </TouchableOpacity>
        </View>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: "#DFDFDF",
  },
  container2: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: "#A4E3F9",
    width: "90%",
    borderRadius: 15,
    borderWidth: 1,
    borderColor: "#004A85",
    marginTop: "1%",
    marginBottom: "5%",
  },
  container3: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
    width: "100%",
  },
  data: {
    backgroundColor: "#ffffff",
    alignItems: "center",
    padding: 10,
    height: "20%",
    width: "30%",
  },
  input: {
    backgroundColor: "#F5F5F5",
    width: "50%",
    marginBottom: 15,
    color: "#000000",
    fontSize: 17,
    borderRadius: 10,
    borderColor: "#004A85",
    borderWidth: 1,
    padding: 10,
  },
  btn: {
    backgroundColor: "#004A85",
    width: "50%",
    height: 45,
    alignItems: "center",
    justifyContent: "center",
    borderRadius: 10,
  },
  textBtn: {
    color: "#ffffff",
    fontSize: 18,
  },
});
