import {
  StyleSheet,
  Text,
  View,
  TouchableOpacity,
  TextInput,
} from "react-native";
import { Card } from "react-native-paper";
import { React, useState } from "react";
import DropDownPicker from "react-native-dropdown-picker";


export default function Home() {
  const [open, setOpen] = useState(false);
  const [items, setItems] = useState([
    { label: "Solteiro(a)", value: "Solteiro(a)" },
    { label: "Casado(a)", value: "Casado(a)" },
    { label: "Separado(a)", value: "Separado(a)" },
    { label: "Divorciado(a)", value: "Divorciado(a)" },
    { label: "Viúvo(a)", value: "Viúvo(a)" },
  ]);

  return (
    <View style={styles.container}>
      <View style={styles.container2}>
        <Card style={styles.card}>
          <View>
            <Text style={{ fontSize: 30 }}>Cadastro de Passageiros</Text>
          </View>
          <View style={styles.form}>
            <TextInput
              style={styles.input}
              placeholder="Digite o nome completo"
            />
            <TextInput
              style={styles.input}
              placeholder="Digite o CPF"
            />
            <TextInput
              style={styles.input}
              placeholder="Digite o RG"
            />
            <DropDownPicker
              style={styles.input}
              open={open}
              setOpen={setOpen}
              items={items}
              placeholder="Escolha um estado cívil"
            />
            <TextInput
              style={styles.input}
              placeholder="Digite a data de nascimento"
            />
            <TextInput
              style={styles.input}
              placeholder="Digite o CEP"
            />
             <TextInput
              style={styles.input}
              placeholder="Digite o CEP"
            />
            <TouchableOpacity>
              <Text>Cadastrar</Text>
            </TouchableOpacity>
          </View>
        </Card>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: "#DFDFDF",
  },
  container2: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: "#A4E3F9",
    width: "90%",
    maxHeight: "95%",
    borderRadius: 15,
    borderWidth: 1,
    borderColor: "#004A85",
  },
  card: {
    flex: 1,
    backgroundColor: "#DFDFDF",
    width: "90%",
    maxHeight: "98%",
    borderRadius: 15,
    borderWidth: 1,
    borderColor: "#004A85",
    padding: 15,
  },
  form: {
    marginTop: 20,
  },
  input: {
    backgroundColor: "#FFFFFF",
    borderRadius: 15,
    borderWidth: 1,
    borderColor: "#004A85",
    padding: 5,
    paddingLeft: 10,
    width: "50%",
  },
});
