import {
  StyleSheet,
  Text,
  View,
  TextInput,
  TouchableOpacity,
} from "react-native";
import { React, useEffect } from "react";
import estilo from "./css";
import { TextInputMask } from "react-native-masked-text";
import SelectDropdown from "react-native-select-dropdown";
import { Ionicons } from "@expo/vector-icons";

export default function CadVeiculo({ navigation }) {
  const Status = ["Em manutenção", "Disponível", "Ocupado"];
  const Regular = ["Regularizado", "Pendente"];
  return (
    <View style={estilo.background}>
      <View style={estilo.container}>
        <Text style={estilo.titulo}>Cadastre um novo veículo</Text>
        <View style={estilo.container3}>
          <TextInput style={estilo.input} placeholder="Digite o modelo" />
          <TextInput style={estilo.input} placeholder="Digite o consumo" />
          <TextInput style={estilo.input} placeholder="Digite a capacidade (Ex.: 10, 20...)" keyboardType="numeric"  maxLength={3}/>
          <Text style={estilo.txtForm}>Data de fabricação</Text>
          <TextInputMask
            style={estilo.input}
            placeholder="DD.MM.AAAA"
            type={"custom"}
            options={{
              mask: "99.99.9999",
            }}
            keyboardType="numeric"
            maxLength={8}
          />
          <Text style={estilo.txtForm}>Status</Text>
          <SelectDropdown
            data={Status}
            onSelect={(selectedItem, index) => {
              //updateForm({ Volta: selectedItem });
            }}
            defaultButtonText={"Status"}
            buttonTextAfterSelection={(selectedItem, index) => {
              return selectedItem;
            }}
            rowTextForSelection={(item, index) => {
              return item;
            }}
            renderDropdownIcon={(isOpened) => {
              return (
                <Ionicons
                  name={isOpened ? "chevron-up" : "chevron-down"}
                  color={"#444"}
                  size={18}
                />
              );
            }}
            dropdownIconPosition={"right"}
            dropdownStyle={estilo.DropdownStyle}
            buttonStyle={estilo.drop}
          />
          <Text style={estilo.txtForm}>Regularização</Text>
          <SelectDropdown
            data={Regular}
            onSelect={(selectedItem, index) => {
              //updateForm({ Volta: selectedItem });
            }}
            defaultButtonText={"Regularização"}
            buttonTextAfterSelection={(selectedItem, index) => {
              return selectedItem;
            }}
            rowTextForSelection={(item, index) => {
              return item;
            }}
            renderDropdownIcon={(isOpened) => {
              return (
                <Ionicons
                  name={isOpened ? "chevron-up" : "chevron-down"}
                  color={"#444"}
                  size={18}
                />
              );
            }}
            dropdownIconPosition={"right"}
            dropdownStyle={estilo.DropdownStyle}
            buttonStyle={estilo.drop}
          />

          <TouchableOpacity style={estilo.btn}>
            <Text style={estilo.textBtn}>Enviar</Text>
          </TouchableOpacity>
        </View>
      </View>
    </View>
  );
}
