import { StyleSheet, Text, View } from "react-native";
import { Card } from "react-native-paper";
import { React, useEffect } from "react";
import { Ionicons } from "@expo/vector-icons";
import { getAuth, onAuthStateChanged, signOut } from "firebase/auth";
import estilo from "./css"

export default function Home({ navigation }) {
  useEffect(() => {
    const auth = getAuth();
    onAuthStateChanged(auth, (user) => {
      if (user) {
        navigation.navigate("Home");
      } else {
        signOut(auth).then(() => {
          navigation.navigate("Login");
        })
      }
    });
  }, []);

  return (
    <View style={estilo.background}>
      <View style={estilo.container}>
        <Text style={{ padding: 10, fontWeight: "bold", fontSize: 25 }}>
          Seja bem vindo(a)!
        </Text>
        <View style={estilo.cardsH}>
          <Card
            style={estilo.cardH}
            onPress={() => navigation.navigate("Consultar Mensalidade")}
          >
            <Text
              style={{ textAlign: "center", fontSize: 15, color: "#004A85" }}
            >
              Consultar as mensalidades
            </Text>
            <Ionicons
              name="ios-document-text-outline"
              size={45}
              color={"#004A85"}
              style={estilo.icon}
            />
          </Card>
          <Card
            style={estilo.cardH}
            onPress={() => navigation.navigate("Registar Viagem")}
          >
            <Text
              style={{ textAlign: "center", fontSize: 15, color: "#004A85" }}
            >
              Registrar horário de viagem
            </Text>
            <Ionicons
              name="md-time-outline"
              size={45}
              color={"#004A85"}
              style={estilo.icon}
            />
          </Card>
          <Card
            style={estilo.cardH}
            onPress={() => navigation.navigate("Efetuar Pagamento")}
          >
            <Text
              style={{ textAlign: "center", fontSize: 15, color: "#004A85" }}
            >
              Efetuar pagamentos
            </Text>
            <Ionicons
              name="md-cash-outline"
              size={45}
              color={"#004A85"}
              style={estilo.icon}
            />
          </Card>
        </View>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: "#DFDFDF",
  },
  container2: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: "#A4E3F9",
    width: "90%",
    borderRadius: 15,
    borderWidth: 2,
    borderColor: "#004A85",
    marginTop: "1%",
    marginBottom: "5%",
    shadowColor: "black",
    shadowOffset: {
      width: 0,
      height: 12,
    },
    shadowOpacity: 0.58,
    shadowRadius: 16.0,
    elevation: 24,
  },
  
});
