import {
  StyleSheet,
  Text,
  View,
  TouchableOpacity,
  TextInput,
} from "react-native";
import { Card } from "react-native-paper";
import { React, useState } from "react";
import { useForm, Controller } from "react-hook-form";
import DropDownPicker from "react-native-dropdown-picker";

export default function Home() {
  const [open, setOpen] = useState(false);
  const [items, setItems] = useState([
    { label: "Solteiro(a)", value: "Solteiro(a)" },
    { label: "Casado(a)", value: "Casado(a)" },
    { label: "Separado(a)", value: "Separado(a)" },
    { label: "Divorciado(a)", value: "Divorciado(a)" },
    { label: "Viúvo(a)", value: "Viúvo(a)" },
  ]);

  const {
    control,
    handleSubmit,
    formState: { errors },
  } = useForm({});

  function CadPassageiro(data) {
    console.log(data);
  }

  return (
    <View style={styles.container}>
      <View style={styles.container2}>
        <Card style={styles.card}>
          <View>
            <Text style={{ fontSize: 30 }}>Cadastro de Passageiros</Text>
          </View>
          <View style={styles.form}>
            <Controller
              control={control}
              name="nome"
              render={({ field: { onChange, onBlur, value } }) => (
                <TextInput
                  style={styles.input}
                  onChangeText={onChange}
                  onBlur={onBlur}
                  value={value}
                  placeholder="Digite o nome completo"
                />
              )}
            />
            <Controller
              control={control}
              name="CPF"
              render={({ field: { onChange, onBlur, value } }) => (
                <TextInput
                  style={styles.input}
                  onChangeText={onChange}
                  onBlur={onBlur}
                  value={value}
                  placeholder="Digite o CPF"
                />
              )}
            />
            <Controller
              control={control}
              name="RG"
              render={({ field: { onChange, onBlur, value } }) => (
                <TextInput
                  style={styles.input}
                  onChangeText={onChange}
                  onBlur={onBlur}
                  value={value}
                  placeholder="Digite o RG"
                />
              )}
            />
            <Controller
              control={control}
              name="estadoCivil"
              render={({ field: { onChange, onBlur, value } }) => (
                <DropDownPicker
                  style={styles.input}
                  open={open}
                  setOpen={setOpen}
                  onChangeText={onChange}
                  onBlur={onBlur}
                  value={value}
                  items={items}
                  placeholder="Escolha um estado cívil"
                />
              )}
            />

            <TouchableOpacity onPress={handleSubmit(CadPassageiro)}>
              <Text>Cadastrar</Text>
            </TouchableOpacity>
          </View>
        </Card>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: "#DFDFDF",
  },
  container2: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: "#A4E3F9",
    width: "90%",
    maxHeight: "95%",
    borderRadius: 15,
    borderWidth: 1,
    borderColor: "#004A85",
  },
  card: {
    flex: 1,
    backgroundColor: "#DFDFDF",
    width: "90%",
    maxHeight: "98%",
    borderRadius: 15,
    borderWidth: 1,
    borderColor: "#004A85",
    padding: 15,
  },
  form: {
    marginTop: 20,
  },
  input: {
    backgroundColor: "#FFFFFF",
    borderRadius: 15,
    borderWidth: 1,
    borderColor: "#004A85",
    padding: 5,
    paddingLeft: 10,
    width: "50%",
  },
});
